
import java.util.Scanner;

public class Codeup39 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		long a=in.nextLong();
		long b=in.nextLong();
		double c=a;
		double d=b;
		double f=c/d;
				
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		System.out.printf("%.2f",f);
	}

}
